import axios from 'axios';
import { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate} from 'react-router-dom';
import { Container, Box, Typography, Button } from '@mui/material';
import Login from "./components/Login";
import Signup from "./components/Signup";
import UserProfile from "./components/UserProfile";
import AddLog from "./components/AddLog";

function App() {
  const [user, setUser] = useState(null);
  const [showSignup, setShowSignup] = useState(false);

  // Fetch current user with JWT token if available
  const fetchUser = async () => {
  const token = localStorage.getItem("token");
  if (!token) {
    setUser(null);
    return;
  }

  try {
    const res = await axios.get("http://203.29.242.50:8000/users/me", {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    });
    setUser(res.data);  // axios puts the response body in res.data

  } catch (error) {
    console.error("Error fetching user:", error);
    setUser(null);
  }
};

  useEffect(() => {
    fetchUser();
  }, []);

  // Called after successful login to reload user info
  const handleLogin = () => {
    fetchUser();
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
  };

  return (



      <Box sx={{ 
                minHeight: '100%', 
                minWidth: '100%',
                backgroundColor: '#f0f2f5', 
                py: 5,
                overflowX: "hidden", 
                overflowY: "hidden", 
                }}>
          <Routes>
            <Route
              path="/"
              element={
                user ? (
                  <Navigate to="/profile" replace />
                ) : showSignup ? (
                  <>
                    <Signup onSignup={() => setShowSignup(false)} />
                    <Typography mt={2} sx={{ textAlign: 'center' }}>
                      Already have an account?{" "}
                      <Button onClick={() => setShowSignup(false)} variant="text">Login</Button>
                    </Typography>
                  </>
                ) : (
                  <>
                    <Login onLogin={handleLogin} />
                    <Typography mt={2} sx={{ textAlign: 'center' }}>
                      Don't have an account?{" "}
                      <Button onClick={() => setShowSignup(true)} variant="text">Sign Up</Button>
                    </Typography>
                  </>
                )
              }
            />

            <Route
              path="/profile"
              element={
                user ? (
                  <>
                    <UserProfile user={user} handleLogout={handleLogout} />
                  </>
                ) : (
                  <Navigate to="/" replace />
                )
              }
            />

            <Route
              path="/AddLog"
              element={
                user ? <AddLog user={user} /> : <Navigate to="/" replace />
              }
            />
          </Routes>
        </Box>
  );
}
export default App;
